mapper.insert:
INSERT INTO NOTICE
(
    NOTICE_SEQ, TITLE,  CONTENTS, USER_ID, READ_CNT,
    REG_DT, CHG_ID, CHG_DT, USER_AREA
)
VALUES(
              (SELECT IFNULL(MAX(A.NOTICE_SEQ),0)+1 FROM NOTICE A),
              #{title},  #{contents}, #{user_id}, 0, NOW(), #{user_id}, NOW(), #{user_area}
      )
